const useStyles = makeStyles({
    header: {
        width: "100%",
        height: "10%",
        background: "#49423F",
        padding: "15px",
        margin: "none",
        position: "fixed",
        top: 0,
        left: 0,
    },
    background:{
        background: "#49423F",
    },
    boxButton: {
       verticalAlign: "-webkit-baseline-middle"
    },
    width100: {
        width: "100%",
        display: "flex"
    },
    width50: {
        width: "50%",
        alignSelf: "center"
    },
    imgDiv: {
        textAlignLast: "center",
        paddingRight: "10px"

    },
    img:{
        height: "120px"
    }
  });

function Header(){
    const classes = useStyles()
    return(
        <Grid container spacing={2} className={classes.background}>
            <Grid item xs={2} md={6} lg={8} className={classes.background}>
                <img src={perfil} className={classes.img}/>
            </Grid>
            <Grid item xs={2} md={6} lg={8}>
                <Typography variant="h3">Ol√°, Vanessa</Typography>
            </Grid>
        </Grid>
    )
}
export default Header
