import { Box, Container, Grid, Paper, Typography, makeStyles } from "@material-ui/core";
import perfil from "../../assets/perfil.png"


const useStyles = makeStyles({
    header: {
        width: "100%",
        height: "10%",
        background: "#49423F",
        padding: "15px",
        margin: "none",
        position: "fixed",
        top: 0,
        left: 0,
    },
    background:{
        background: "#49423F",
    },
    title: {
        color: "#fff",
        alignSelf: "center"
    },
    img:{
        height: "120px"
    }
  });

function Header(){
    const classes = useStyles()
    return(
        <Grid container spacing={2} className={classes.background}>
            <Grid item xs={6} md={3} lg={1} className={classes.background}>
                <img src={perfil} className={classes.img}/>
            </Grid>
            <Grid item xs={6} md={3} lg={2}>
                <Typography variant="h3" className={classes.title}>Ol√°, Vanessa</Typography>
            </Grid>
        </Grid>
    )
}
export default Header
