import { Button, Card, CardActionArea, CardActions, CardContent, Container, ListItem, ListItemText, Typography, makeStyles } from "@material-ui/core"
import data from "../../assets/listTask/list.json"
import { useDispatch } from "react-redux"
import { FixedSizeList } from 'react-window'

const useStyles = makeStyles({
    root: {
        maxWidth: 345,
      },
      media: {
        height: 140,
      },
      alignContent: {
        alignContent: "center",
        width: "100vh",
      },
      scrollBar: {
        overflowY: "scroll",
        overflowX: "auto"
      }
})

const renderRow = (props, classes, status) => {
    const { index, style } = props;
    console.log("status ", status )
    return(
        <ListItem>
            <ListItemText button style={style} key={index}>
                {data[index].status === status ??
                    <Card className={classes.root}>
                        <CardActionArea>
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {data[index].title}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    {data[index].desc}
                                </Typography>
                            </CardContent>
                        </CardActionArea>
                        <CardActions>
                            <Button size="small" color="primary">
                                Excluir
                            </Button>
                            <Button size="small" color="primary">
                                Iniciar tarefa
                            </Button>
                        </CardActions>
                    </Card>
                }
            </ListItemText>
        </ListItem>
    )
}

function ListCardTask({status}){
    const classes = useStyles()

    return(
        <Container className={`${classes.alignContent}`}>
            <FixedSizeList
                height={600} 
                width={400} 
                itemSize={180} 
                itemCount={17} 
            >
                {(props) => renderRow(props, classes, status)}
            </FixedSizeList>
        </Container>
    )
}
export default ListCardTask
