import { Box, Container, Grid, Paper, Typography, makeStyles } from "@material-ui/core";
import perfil from "../../assets/perfil.png"


const useStyles = makeStyles({
    header: {
        width: "100%",
        height: "10%",
        background: "#49423F",
        padding: "15px",
        margin: "none",
        position: "fixed",
        top: 0,
        left: 0,
    },
    boxButton: {
       verticalAlign: "-webkit-baseline-middle"
    },
    width100: {
        width: "100%",
        display: "flex"
    },
    width50: {
        width: "50%",
        alignSelf: "center"
    },
    imgDiv: {
        textAlignLast: "center",
        paddingRight: "10px"

    },
    img:{
        height: "120px"
    }
  });

function Header(){
    const classes = useStyles()
    return(
        <header className={classes.header}>
             <Grid container spacing={2}>
                {/* Primeiro Grid */}
                <Grid item xs={6}>
                <Paper style={{ padding: 20, textAlign: 'center' }}>
                    <Typography variant="h6">Grid 1</Typography>
                    <h1>tese</h1>
                </Paper>
                </Grid>

                {/* Segundo Grid */}
                <Grid item xs={6}>
                <Paper style={{ padding: 20, textAlign: 'center' }}>
                    <Typography variant="h6">Grid 2</Typography>
                    <h1>DWF</h1>
                </Paper>
                </Grid>
            </Grid>


            <Box className={classes.boxButton}>
                <div className={classes.width100}>
                    <div className={classes.width50}>
                        <img src={perfil} className={classes.img}/>
                        <Typography variant="h">Ol√°, Vanessa</Typography>
                    </div>
                    <div className={`${classes.width50} ${classes.imgDiv}`}>

                    </div>
                </div>
                <Container>

                </Container>
            </Box>
        </header>
    )
}
export default Header
