import { Box, Container, CssBaseline, Grid, Paper, makeStyles, Pagination } from "@material-ui/core"
import Header from "../../componets/header"
import { useState } from "react";

const useStyles = makeStyles({
    paddingTop10: {
        paddingTop: "10%",
    },
    alingPaper: {
        padding: 20, 
        textAlign: 'center',
    },
    box: {
        backgroundColor: "#E1E1E1"
    },
    height100: {
        height: "80vh"
    }
})

function PageMain(){
    const classes = useStyles()
    const [currentPage, setCurrentPage] = useState(1);

    const handleChangePage = (event, newPage) => {
      setCurrentPage(newPage);
    };

    return (
      <Container component="main" maxWidth="lg" style={{ height: '100vh' }}>
        <CssBaseline />
        <Grid container spacing={2} style={{ height: '100%' }}>
          {/* Primeira coluna */}
          <Grid item xs={12} sm={12}>
            <Paper style={{ padding: 20, textAlign: 'center', height: '100%' }}>
              Conteúdo da Coluna 1
            </Paper>
          </Grid>

          {/* Segunda coluna */}
          <Grid item xs={12} sm={12}>
            <Paper style={{ padding: 20, textAlign: 'center', height: '100%' }}>
              Conteúdo da Coluna 2
            </Paper>
          </Grid>

          {/* Terceira coluna */}
          <Grid item xs={12} sm={12}>
            <Paper style={{ padding: 20, textAlign: 'center', height: '100%' }}>
              Conteúdo da Coluna 3
            </Paper>
          </Grid>
        </Grid>

        {/* Pagination para navegar lateralmente em telas menores */}
        <Pagination
          count={3}  // número total de páginas (número de grids)
          page={currentPage}
          onChange={handleChangePage}
          style={{ marginTop: '20px', textAlign: 'center' }}
        />
      </Container>
    );
  };

export default PageMain
