import { Box, Container, Grid, Paper, Typography, makeStyles } from "@material-ui/core";
import perfil from "../../assets/perfil.png"


const useStyles = makeStyles({
    header: {
        width: "100%",
        height: "10%",
        background: "#49423F",
        padding: "15px",
        margin: "none",
        position: "fixed",
        top: 0,
        left: 0,
    },
    background:{
        background: "#49423F",
    },
    boxButton: {
       verticalAlign: "-webkit-baseline-middle"
    },
    width100: {
        width: "100%",
        display: "flex"
    },
    width50: {
        width: "50%",
        alignSelf: "center"
    },
    imgDiv: {
        textAlignLast: "center",
        paddingRight: "10px"

    },
    img:{
        height: "120px"
    }
  });

function Header(){
    const classes = useStyles()
    return(
        <header className={classes.header}>
             <Grid container spacing={2} className={classes.background}>
                <Grid item xs={6}>
                    <Paper style={{ padding: 20, textAlign: 'center' }}>
                        <img src={perfil} className={classes.img}/>
                    </Paper>
                </Grid>
                <Grid item xs={6}>
                    <Paper style={{ padding: 20, textAlign: 'center' }}>
                        <Typography variant="h1">Ol√°, Vanessa</Typography>
                    </Paper>
                </Grid>
            </Grid>
        </header>
    )
}
export default Header
